if(msg.event.sQuery && msg.event.sBinds){
    node.status({fill:"green",shape:"ring",text:"executing sql"});
    let db = global.get(env.get('DB_CONTEXT'));
    db.connection.query(msg.event.sQuery,
    {
        bind: msg.event.sBinds
    })
    .then(([results, metadata])  => {
        node.status({});
        msg.payload = results;
        msg.metadata = metadata;
        msg.error = false;
        node.send(msg);
    }, error=>{
        node.status({fill:"red",shape:"dot",text:"SQL Error"});
        msg.payload = [];
        msg.metadata = {};
        msg.error = error;
        node.send(msg);
    });
}else{
    msg.error = "No query(sQuery) or Binds(sBinds) found.";
    node.status({fill:"red",shape:"ring",text:"no query or binds provided"});
    node.send(msg);
}

msg.event = {
    sQuery : msg.sQuery || env.get('Query'),
    sBinds : msg.sBinds || env.get('Parameters') || {},
};
return msg;

